import React, {Component} from 'react'
//import DataService from '../../api/todo/DataService.js'
import SearchBar  from './SearchBar.jsx'
import axios from 'axios'
import { handleDefaults } from 'plotly.js/lib/calendars';

class UserComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            users : []
        };
        let amountInput = React.createRef()
    }
componentDidMount(){
this.getUser();
}
getUser(){
    axios.get("http://localhost:8080/users")
    //.then(response => console.log(response.data))
    .then(response => response.data)
    .then((response) => {
          this.setState({users : response.data})
     })
}
handleDeposit = userName => {
    // const user = this.props.filter(c => c.userId === userId)
    // const deposit = () => 
    console.log(this.props.userName)
}
handleClick() {
    console.log(amountInput.current.value);
}

render() {
    return (
        <div>
             <h1>User Detail</h1>
              <SearchBar/> 
             <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>FirstName</th>
                            <th>LastName</th>
                            <th>Balance</th>                   
                        </tr>
                    </thead>
                    <tbody>
                    {
                       
                        this.state.users.map (
                            user =>
                                <tr key={user.userId}>
                                    <td>{user.firstName}</td>
                                    <td>{user.lastName}</td>
                                    <td>{user.balance}</td>
                                </tr>
                        )
                        }
                    </tbody>
                </table>
             </div>
             <div ref={amountInput} placeholder='Enter amount'>
                <div onClick={handleClick} className='Deposit'>
                </div>
             </div>
        </div>
    )
  }
}

export default UserComponent
